        function tohalf($num){
            if ($num > (int)$num + 0.5){
                $toHalf = (int)$num + 1;
            }
            elseif (($num <= (int)$num + 0.5) && ($num > (int)$num)){
                $toHalf = (int)$num + 0.5;
            }
            elseif ($num == (int)$num){
                $toHalf = (int)$num;
            }
            else{
                $toHalf = (int)$num;
            }
            return $toHalf;
        }
